func (ass *AuthSessionSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := ass.sqlQuery().Query()
	if err := ass.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (ass *AuthSessionSelect) sqlQuery() sql.Querier {
	selector := ass.sql
	selector.Select(selector.Columns(ass.fields...)...)
	return selector
}
