func (mlgb *MapLayerGroupBy) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range mlgb.fields {
		if !maplayer.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for group-by", f)}
		}
	}
	selector := mlgb.sqlQuery()
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := mlgb.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (mlgb *MapLayerGroupBy) sqlQuery() *sql.Selector {
	selector := mlgb.sql
	columns := make([]string, 0, len(mlgb.fields)+len(mlgb.fns))
	columns = append(columns, mlgb.fields...)
	for _, fn := range mlgb.fns {
		columns = append(columns, fn(selector, maplayer.ValidColumn))
	}
	return selector.Select(columns...).GroupBy(mlgb.fields...)
}

